CREATE OR REPLACE FUNCTION obter_se_proc_exige_lado (nr_seq_proc_interno_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE



ie_exige_lado_w		varchar(01)	:= 'N';


BEGIN

if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') then
	select	coalesce(max(ie_exige_lado), 'N')
	into STRICT		ie_exige_lado_w
	from 		proc_interno
	where		nr_sequencia	= nr_seq_proc_interno_p;
end if;

if (ie_exige_lado_w = 'N') and (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
	select	coalesce(max(ie_exige_lado), 'X')
	into STRICT		ie_exige_lado_w
	from		procedimento
	where		cd_procedimento		= cd_procedimento_p
	and		ie_origem_proced		= ie_origem_proced_p;
elsif (ie_exige_lado_w = 'N') and (coalesce(cd_procedimento_p::text, '') = '') then
	ie_exige_lado_w := 'X';
end if;

return	ie_exige_lado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
