CREATE OR REPLACE FUNCTION obter_se_proc_ficha_tecnica ( cd_estabelecimento_p bigint, nr_seq_proc_interno_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w				varchar(1)	:= 'N';


BEGIN

if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') then
	begin

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	ficha_tecnica a
	where	a.nr_seq_proc_interno	= nr_seq_proc_interno_p
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	and	coalesce(a.cd_procedimento::text, '') = '';

	if (ie_retorno_w = 'N') then

		select	coalesce(max('S'),'N')
		into STRICT	ie_retorno_w
		from	custo_procedimento a
		where	a.nr_seq_proc_interno	= nr_seq_proc_interno_p
		and	a.cd_estabelecimento	= cd_estabelecimento_p
		and	coalesce(a.cd_procedimento::text, '') = '';

	end if;

	end;
elsif (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
	begin

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	ficha_tecnica a
	where	a.cd_procedimento		= cd_procedimento_p
	and	a.ie_origem_proced		= ie_origem_proced_p
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	and	coalesce(a.nr_seq_proc_interno::text, '') = '';

	if (ie_retorno_w = 'N') then

		select	coalesce(max('S'),'N')
		into STRICT	ie_retorno_w
		from	custo_procedimento a
		where	a.cd_procedimento		= cd_procedimento_p
		and	a.ie_origem_proced		= ie_origem_proced_p
		and	a.cd_estabelecimento	= cd_estabelecimento_p
		and	coalesce(a.nr_seq_proc_interno::text, '') = '';

	end if;

	end;
end if;

return	ie_retorno_w;

end;
$body$
