CREATE OR REPLACE FUNCTION obter_se_proc_glosado_atx (Nr_Sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_proc_glosa_w		varchar(1):='N';
qt_glosado_w		bigint;
nr_seq_proc_princ_w	bigint;
BEGIN

select 	coalesce(max(nr_seq_proc_princ),0)
into STRICT	nr_seq_proc_princ_w
from 	procedimento_paciente
where	nr_sequencia = nr_sequencia_p;

select 	count(*)
into STRICT	qt_glosado_w
from	procedimento_paciente
where	nr_sequencia = nr_seq_proc_princ_w
and 	cd_situacao_glosa = 8;

if (qt_glosado_w > 0) then
	ie_proc_glosa_w:= 'S';
end if;

return	ie_proc_glosa_w;

end;
$body$
LANGUAGE PLPGSQL
