SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_proc_hor_lote (nr_seq_horario_p bigint, nr_seq_processo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
nr_seq_lote_w		prescr_mat_hor.nr_seq_lote%type;
ie_origem_lote_w	ap_lote.ie_origem_lote%type;


BEGIN

if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') and (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') then

	select	coalesce(max(nr_seq_lote),0)
	into STRICT	nr_seq_lote_w
	from	prescr_mat_hor
	where 	nr_seq_processo	=	nr_seq_processo_p
	and	nr_sequencia	=	nr_seq_horario_p
	and	(nr_seq_lote IS NOT NULL AND nr_seq_lote::text <> '');


	if (nr_seq_lote_w > 0) then

		select 	ie_origem_lote
		into STRICT	ie_origem_lote_w
		from	ap_lote
		where	nr_sequencia = nr_seq_lote_w;


		if (ie_origem_lote_w = 'GLPG') then
			ds_retorno_w	:= 'N';
		else
			ds_retorno_w	:= 'S';
		end if;


	else
		ds_retorno_w 	:= 'N';
	end if;


end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
