SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_proc_internacao ( nr_seq_proc_interno_p bigint, dt_nascimento_pac_p timestamp) RETURNS varchar AS $body$
DECLARE



qt_meses_w				varchar(20);
ie_necessita_internacao_w			varchar(1) := 'N';

BEGIN

if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') and (dt_nascimento_pac_p IS NOT NULL AND dt_nascimento_pac_p::text <> '') then
	begin

	select	substr(obter_idade(dt_nascimento_pac_p,clock_timestamp(),'M'),1,20)
	into STRICT	qt_meses_w
	;


	select	coalesce(max(ie_necessita_internacao),'N')
	into STRICT	ie_necessita_internacao_w
	from  	proc_int_regra_inter_age
	where 	nr_seq_proc_interno	 = 	nr_seq_proc_interno_p
	and	qt_meses_w between qt_idade_min and qt_idade_max;

	end;
end if;



return	ie_necessita_internacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
