CREATE OR REPLACE FUNCTION obter_se_proc_int_lib ( nr_seq_proc_interno_p bigint, cd_funcao_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(10):= 'S';
qt_reg_w	bigint;
cd_setor_w	bigint;
cd_estabelecimento_w	bigint;
nr_seq_regra_grupo_W	bigint;
cd_cargo_w				bigint;
cd_setor_atendimento_w	usuario.cd_setor_atendimento%type;


C01 CURSOR FOR
	SELECT	coalesce(nr_seq_regra_grupo,0)
	from	REGRA_GRUPO_USUARIO_BLOQ
	where	coalesce(cd_usuario, nm_usuario_p) = nm_usuario_p
	and		coalesce(cd_cargo, cd_cargo_w) = cd_cargo_w
	and		coalesce(cd_perfil, cd_perfil_p)	= cd_perfil_p
	and		coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_w, 0)) = coalesce(cd_setor_atendimento_w, 0)
	order by coalesce(cd_usuario,0), coalesce(cd_cargo, 0), coalesce(cd_perfil, 0), coalesce(cd_setor_atendimento, 0);

C02 CURSOR FOR
	SELECT	coalesce(ie_permite,'N')
	from	PROC_INT_REGRA_FUNCAO
	where	nr_seq_proc_interno		= nr_seq_proc_interno_p
	and	cd_funcao			= cd_funcao_p
	and	cd_estabelecimento		= cd_estabelecimento_w
	and	coalesce(cd_perfil,cd_perfil_p)	= cd_perfil_p
	and	coalesce(nr_seq_regra_grupo, coalesce(nr_seq_regra_grupo_W,0)) = coalesce(nr_seq_regra_grupo_W,0)
	and	coalesce(nm_usuario_regra,nm_usuario_p)= nm_usuario_p
	order by coalesce(cd_perfil,0),	coalesce(nm_usuario_regra,'A');


BEGIN
cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;

select	max(p.cd_cargo)
into STRICT	cd_cargo_w
from	usuario u,
	pessoa_fisica p
where	u.nm_usuario = nm_usuario_p
and	u.cd_pessoa_fisica = p.cd_pessoa_fisica;

select  cd_setor_atendimento
into STRICT	cd_setor_atendimento_w
from    usuario
where   nm_usuario = nm_usuario_p;

open C01;
loop
fetch C01 into
	nr_seq_regra_grupo_W;
EXIT WHEN NOT FOUND; /* apply on C01 */
	nr_seq_regra_grupo_W	:= nr_seq_regra_grupo_W;
end loop;
close C01;

open C02;
loop
fetch C02 into
	ds_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	ds_retorno_w := ds_retorno_w;
end loop;
close C02;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
