SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_proc_int_setor_usu ( nr_seq_proc_interno_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;


BEGIN

if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') then

	select	cd_procedimento,
		ie_origem_proced
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w
	from	proc_interno
	where	nr_sequencia = nr_seq_proc_interno_p;


	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from	procedimento
	where	cd_procedimento = cd_procedimento_w
	and	ie_origem_proced = ie_origem_proced_w
	and	cd_setor_exclusivo in (	SELECT 	x.cd_setor_atendimento
					from   	usuario_setor_v x
					where  	x.nm_usuario	= nm_usuario_p);

	if (ie_retorno_w = 'N') then

		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_retorno_w
		from	proc_interno_setor p,
			usuario_setor_v u
		where	p.nr_seq_proc_interno 	= nr_seq_proc_interno_p
		and	u.nm_usuario		= nm_usuario_p
		and	u.cd_setor_atendimento	= p.cd_setor_atendimento
		and	coalesce(cd_perfil,obter_perfil_ativo) = obter_perfil_ativo;


	end if;


end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
