CREATE OR REPLACE FUNCTION obter_se_proc_lib_exec_pepo ( cd_perfil_p bigint, nr_seq_proc_interno_p bigint, ie_origem_proced_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(1) := 'S';
ie_tipo_util_w		varchar(15);
nr_seq_classif_w	bigint;
qt_regra_lib_w		bigint := 0;


BEGIN
begin

if (coalesce(cd_perfil_p,0) > 0) then

	select count(*)
	into STRICT   qt_regra_lib_w
	from   proced_lib_exec_cirur
	where  cd_perfil = cd_perfil_p
	and (coalesce(IE_SITUACAO,'A') = 'A');

end if;

if (qt_regra_lib_w > 0) then

	if (coalesce(nr_seq_proc_interno_p,0) > 0) then
		select  nr_seq_classif,
			ie_tipo_util
		into STRICT	nr_seq_classif_w,
			ie_tipo_util_w
		from 	proc_interno
		where 	nr_sequencia = nr_seq_proc_interno_p;
	end if;

	select  coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from    proced_lib_exec_cirur
	where   cd_perfil = cd_perfil_p
	AND	coalesce(nr_seq_proc_interno,nr_seq_proc_interno_p)= nr_seq_proc_interno_p
	AND	coalesce(ie_tipo_util,coalesce(ie_tipo_util_w,'XPTO')) = coalesce(ie_tipo_util_w,'XPTO')
	AND	coalesce(nr_seq_classif,coalesce(nr_seq_classif_w,0)) = coalesce(nr_seq_classif_w,0);

end if;

exception
when others then
	ds_retorno_w := 'S';
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
