CREATE OR REPLACE FUNCTION obter_se_proc_mat_repasse (nr_seq_item_p bigint, ie_proc_mat_p text) RETURNS varchar AS $body$
DECLARE


ie_item_repasse_w	varchar(1) := 'N';

BEGIN
if (ie_proc_mat_p = 'P') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_item_repasse_w
	from	procedimento_repasse x
	where	x.nr_seq_procedimento = nr_seq_item_p;

elsif (ie_proc_mat_p = 'M') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_item_repasse_w
	from	material_repasse x
	where	x.nr_seq_material = nr_seq_item_p;

end if;


return	ie_item_repasse_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
