CREATE OR REPLACE FUNCTION obter_se_proc_nurse_pract ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(1) := 'N';
qt_registro_w bigint := 0;


BEGIN

	select count(1)
	into STRICT qt_registro_w
	from PROC_PAC_DESCRICAO
	where nr_atendimento = nr_atendimento_p
	and  IE_ORIGEM_PROCED = 20
	and (obter_dados_estrut_proc(cd_procedimento,ie_origem_proced,'C','G'))::numeric  in (SELECT cd_grupo_proc FROM grupo_proc where CD_SISTEMA_ANT like 'M14%')
	and ie_situacao = 'A'
	and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and coalesce(dt_inativacao::text, '') = '';


if (qt_registro_w > 0) then

	ds_retorno_w := 'S';

end if;


return ds_retorno_w;

end;
