CREATE OR REPLACE FUNCTION obter_se_proc_padrao_liberado (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) 	:= 'S';

qt_regra_w		bigint;
qt_regra_perfil_w 	bigint;
ie_restringe_perfil_w	varchar(1);
qt_restringe_perfil_w	varchar(1)	:= 0;

C01 CURSOR FOR
	SELECT 	coalesce(ie_restringe_perfil,'N')
	from 	conv_proced_padrao_perfil
	where 	nr_seq_proc_padrao = nr_sequencia_p;


BEGIN

select 	count(*)
into STRICT	qt_regra_w
from 	conv_proced_padrao_perfil
where 	nr_seq_proc_padrao = nr_sequencia_p;

if (qt_regra_w > 0) then
	open C01;
	loop
	fetch C01 into
		ie_restringe_perfil_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (ie_restringe_perfil_w = 'S') then
			qt_restringe_perfil_w := 1;
		end if;
		end;
	end loop;
	close C01;


	if (qt_restringe_perfil_w = 1) then

		ds_retorno_w:= 'S';
		select 	count(*)
		into STRICT	qt_regra_perfil_w
		from 	conv_proced_padrao_perfil
		where 	nr_seq_proc_padrao = nr_sequencia_p
		and 	cd_perfil = obter_perfil_ativo
		and	coalesce(ie_restringe_perfil,'N') = 'S';

		if (qt_regra_perfil_w > 0) then
			ds_retorno_w:= 'N';
		end if;


	else
		ds_retorno_w:= 'N';
		select 	count(*)
		into STRICT	qt_regra_perfil_w
		from 	conv_proced_padrao_perfil
		where 	nr_seq_proc_padrao = nr_sequencia_p
		and 	cd_perfil = obter_perfil_ativo
		and	coalesce(ie_restringe_perfil,'N') = 'N';

		if (qt_regra_perfil_w > 0) then
			ds_retorno_w:= 'S';
		end if;

	end if;
end if;
return	ds_retorno_w;

end;
$body$
