SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_proc_por_nivel ( nr_seq_aprovacao_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_aprovacao_nivel_param_w	varchar(15)	:= 'N';
ie_aprovacao_nivel_w		varchar(1)	:= 'N';
nr_seq_proc_aprov_w			processo_aprov_compra.nr_seq_proc_aprov%type;
nr_documento_w            		processo_aprov_compra.nr_documento%type;
qt_nova_estrutura_w        		bigint;


BEGIN

select	coalesce(ie_aprovacao_nivel,'N')
into STRICT	ie_aprovacao_nivel_param_w
from	parametro_compras
where	cd_estabelecimento = cd_estabelecimento_p;

select	max(nr_seq_proc_aprov),
	max(nr_documento)
into STRICT	nr_seq_proc_aprov_w,
	nr_documento_w
from	processo_aprov_compra
where 	nr_sequencia = nr_seq_aprovacao_p
and     cd_pessoa_fisica = obter_pessoa_fisica_usuario(wheb_usuario_pck.get_nm_usuario,'C');

select 	obter_estrutura_aprovacao(nr_seq_proc_aprov_w, nr_documento_w)
into STRICT	qt_nova_estrutura_w
;

/*Identifica que nos parametros de compras esta utilizando as regras de niveis do processo de aprovacao*/

if (ie_aprovacao_nivel_param_w = 'R') then

	if (qt_nova_estrutura_w > 0) then

		select	coalesce(max(a.ie_aprovacao_nivel),'N')
		into STRICT	ie_aprovacao_nivel_w
		from	processo_aprovacao a,
			processo_aprov_compra b
		where	a.cd_processo_aprov	= b.cd_processo_aprov
		and	b.nr_sequencia		= nr_seq_aprovacao_p;
	else
		select	coalesce(max(a.ie_aprovacao_nivel),'N')
		into STRICT	ie_aprovacao_nivel_w
		from	processo_aprovacao a,
			processo_aprov_resp b,
			processo_aprov_compra c
		where	a.cd_processo_aprov	= b.cd_processo_aprov
		and	c.nr_seq_proc_aprov	= b.nr_sequencia
		and	c.nr_sequencia		= nr_seq_aprovacao_p;
	end if;
end if;

return	ie_aprovacao_nivel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
-- REVOKE ALL ON FUNCTION obter_se_proc_por_nivel ( nr_seq_aprovacao_p bigint, cd_estabelecimento_p bigint) FROM PUBLIC;

