CREATE OR REPLACE FUNCTION obter_se_proc_protocolo ( nr_seq_protocolo_p bigint, nr_seq_proc_interno_p bigint, nr_seq_pepo_p bigint, nr_cirurgia_p bigint default null) RETURNS varchar AS $body$
DECLARE

								
ie_utiliza_w	varchar(1) := 'N';
nr_seq_proc_interno_w	bigint	:= nr_seq_proc_interno_p;


BEGIN

if (nr_seq_pepo_p > 0) then
	
	select	coalesce(max('S'),'N')
	into STRICT	ie_utiliza_w
	FROM protocolo_cirurgia b
LEFT OUTER JOIN proced_protocolo_cirurgia a ON (b.nr_sequencia = a.nr_seq_protocolo)
WHERE b.nr_sequencia 		= nr_seq_protocolo_p and ((coalesce(b.nr_seq_proc_interno, a.nr_seq_proc_interno) in (	SELECT	nr_seq_proc_interno
										from	cirurgia
										where	nr_seq_pepo = nr_seq_pepo_p)) or ((coalesce(b.nr_seq_proc_interno::text, '') = '') and (coalesce(a.nr_seq_proc_interno::text, '') = '')));
									
else

	if (nr_seq_proc_interno_w	= 0) and (nr_cirurgia_p > 0) then
		select	max(nr_seq_proc_interno)
		into STRICT	nr_seq_proc_interno_w
		from	cirurgia
		where	nr_cirurgia = nr_cirurgia_p;
	end if;

	select	coalesce(max('S'),'N')
	into STRICT	ie_utiliza_w
	FROM protocolo_cirurgia b
LEFT OUTER JOIN proced_protocolo_cirurgia a ON (b.nr_sequencia = a.nr_seq_protocolo)
WHERE b.nr_sequencia 		= nr_seq_protocolo_p and ((a.nr_seq_proc_interno = nr_seq_proc_interno_w) or
		((b.nr_seq_proc_interno = nr_seq_proc_interno_w) or 
		((coalesce(b.nr_seq_proc_interno::text, '') = '') and (coalesce(a.nr_seq_proc_interno::text, '') = ''))));
end if;

return	ie_utiliza_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
