CREATE OR REPLACE FUNCTION obter_se_proc_recebe_honorario ( nr_sequencia_p bigint, ie_tipo_atendimento_p bigint, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(15)	:= 'S';
cd_area_proc_w			bigint;
cd_grupo_proc_w			bigint;
cd_especialidade_w		bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
cd_estabelecimento_w		integer;


BEGIN

select	a.cd_procedimento,
	a.ie_origem_proced,
	b.cd_estabelecimento
into STRICT	cd_procedimento_w,
	ie_origem_proced_w,
	cd_estabelecimento_w
from	atendimento_paciente b,
	Procedimento_paciente a
where	a.nr_atendimento	= b.nr_atendimento
and	a.nr_sequencia	= nr_sequencia_p;

begin
select	cd_area_procedimento,
	cd_grupo_proc,
	cd_especialidade
into STRICT	cd_area_proc_w,
	cd_grupo_proc_w,
	cd_especialidade_w
from	estrutura_procedimento_v
where	cd_procedimento	= cd_procedimento_w
and	ie_origem_proced	= ie_origem_proced_w;
exception
	when others then
	cd_area_proc_w		:= 0;
	cd_grupo_proc_w		:= 0;
	cd_especialidade_w	:= 0;
	end;

begin
select	coalesce(max('N'),'S')
into STRICT	ds_retorno_w
from	conv_regra_honor_glosa
where	cd_convenio = cd_convenio_p
and	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_p) 	= ie_tipo_atendimento_p
and	coalesce(cd_area_procedimento,cd_area_proc_w)	= cd_area_proc_w
and	coalesce(cd_especialidade,cd_especialidade_w)	= cd_especialidade_w
and	coalesce(cd_grupo_proc,cd_grupo_proc_w)		= cd_grupo_proc_w
and	coalesce(cd_procedimento,cd_procedimento_w)	= cd_procedimento_w
and	coalesce(ie_origem_proced,ie_origem_proced_w)	= ie_origem_proced_w
and	ie_situacao = 'A';
exception
when others then
	ds_retorno_w := 'S';
end;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
