CREATE OR REPLACE FUNCTION obter_se_proc_rec_alerta ( nr_seq_proc_interno_p bigint, nr_seq_regra_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_exame_p bigint default null) RETURNS varchar AS $body$
DECLARE


ie_recebe_w	varchar(1);
qt_existe_regra_w	bigint;
		

BEGIN

ie_recebe_w := 'S';

select	count(*)
into STRICT	qt_existe_regra_w
from	regra_envio_sms_proc
where	nr_seq_regra = nr_seq_regra_p;
	
if (qt_existe_regra_w = 0) then
	ie_recebe_w := 	'S';
else
	select	coalesce(max('S'),'N')
	into STRICT	ie_recebe_w
	from	regra_envio_sms_proc
	where	coalesce(nr_seq_proc_interno,coalesce(nr_seq_proc_interno_p,0))	= coalesce(nr_seq_proc_interno_p,0)
	and		coalesce(cd_procedimento,coalesce(cd_procedimento_p,0))		= coalesce(cd_procedimento_p,0)
	and		coalesce(ie_origem_proced,coalesce(ie_origem_proced_p,0))		= coalesce(ie_origem_proced_p,0)
	and		coalesce(nr_seq_exame,coalesce(nr_seq_exame_p,0))		= coalesce(nr_seq_exame_p,0)
	and		obter_se_proc_grupo_env(cd_procedimento_p,ie_origem_proced_p,nr_seq_proc_interno_p,nr_seq_exame_p,nr_seq_grupo)	= 'S'
	and		nr_seq_regra = nr_seq_regra_p;
end if;

return	ie_recebe_w;

end;
$body$
