CREATE OR REPLACE FUNCTION obter_se_proc_regra_conta (nr_interno_conta_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);
cd_convenio_w		bigint;
ie_tipo_atendimento_w	varchar(10);
cd_estabelecimento_w	integer;
cd_pessoa_fisica_w	varchar(10);
dt_entrada_w		timestamp;
qt_atend_w		integer;
qt_regra_w		bigint;
qt_dias_w		bigint;
dt_entrada_passada_w	timestamp;
ie_regra_w		varchar(10);
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
qt_dias_per_conta_w	bigint;
qt_proc_w		bigint;
cd_plano_convenio_w	varchar(10);

c02 CURSOR FOR
	SELECT	ie_regra,
		qt_regra,
		cd_procedimento,
		ie_origem_proced
	from	convenio_regra_atend
	where	cd_convenio		= cd_convenio_w
	and	ie_tipo_atendimento	= ie_tipo_atendimento_w
	and	ie_regra		= 'P'
	and	(cd_procedimento IS NOT NULL AND cd_procedimento::text <> '')
	and	coalesce(cd_plano, coalesce(cd_plano_convenio_w, '0')) = coalesce(cd_plano_convenio_w,'0')
	order by cd_procedimento  desc;


BEGIN
ds_retorno_w	:= 'S';

select	max(ie_tipo_atendimento)
into STRICT	ie_tipo_atendimento_w
from	atendimento_paciente
where 	nr_atendimento	= nr_atendimento_p;

select	max(obter_plano_convenio_atend(nr_atendimento_p,'C'))
into STRICT	cd_plano_convenio_w
;

select	max(cd_convenio_parametro),
	max(ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_periodo_final) - ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_periodo_inicial))
into STRICT	cd_convenio_w,
	qt_dias_per_conta_w
from	conta_paciente
where 	nr_interno_conta	= nr_interno_conta_p;
	
open c02;
loop
fetch c02 into
	ie_regra_w,
	qt_regra_w,
	cd_procedimento_w,
	ie_origem_proced_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
	begin

	select	coalesce(sum(qt_procedimento),0)
	into STRICT	qt_proc_w
	from	procedimento_paciente
	where	nr_interno_conta	= nr_interno_conta_p
	and	cd_procedimento		= cd_procedimento_w
	and	ie_origem_proced	= ie_origem_proced_w
	and	coalesce(cd_motivo_exc_conta::text, '') = '';
	
	if (qt_proc_w < qt_dias_per_conta_w) then
		ds_retorno_w	:= 'N';
	end if;

	end;
end loop;
close c02;		

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
