CREATE OR REPLACE FUNCTION obter_se_proc_rotina ( cd_medico_p bigint, ie_tipo_atendimento_p bigint, cd_setor_atendimento_p bigint, cd_classif_setor_pac_p bigint, ie_clinica_p bigint, cd_setor_prescr_p bigint, cd_convenio_p bigint, nr_sequencia_p bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, ie_tipo_convenio_p bigint default null) RETURNS varchar AS $body$
DECLARE

 
 
ds_retorno_w			char(1);
cd_perfil_w			perfil.cd_perfil%type;

C01 CURSOR FOR 
SELECT	coalesce(ie_permite, 'N') 
from	procedimento_rotina_lib a 
where	nr_seq_proc_rotina 		= nr_sequencia_p 
and	coalesce(cd_perfil, cd_perfil_w)	= cd_perfil_w 
and	coalesce(coalesce(cd_estabelecimento, cd_estabelecimento_p), 999)	= coalesce(coalesce(cd_estabelecimento_p, cd_estabelecimento),999) 
and (exists (	SELECT	b.cd_especialidade 
				from	medico_especialidade b 
				where	b.cd_especialidade = a.cd_especialidade 
				and	b.cd_pessoa_fisica = cd_medico_p) or (coalesce(a.cd_especialidade::text, '') = '')) 
and	((ie_tipo_atendimento	= ie_tipo_atendimento_p) or (coalesce(ie_tipo_atendimento::text, '') = '')) 
and	((cd_setor_atendimento = cd_setor_atendimento_p) or (coalesce(cd_setor_atendimento::text, '') = '')) 
and	((ie_tipo_convenio	= ie_tipo_convenio_p) or (coalesce(ie_tipo_convenio::text, '') = '')) 
and	((cd_classif_setor_pac = cd_classif_setor_pac_p) or (coalesce(cd_classif_setor_pac::text, '') = '')) 
and	((ie_clinica		= ie_clinica_p)		or (coalesce(ie_clinica::text, '') = ''))  
and	((cd_setor_prescr	= cd_setor_prescr_p)	or (coalesce(cd_setor_prescr::text, '') = ''))  
and	((cd_convenio		= cd_convenio_p)	or (coalesce(cd_convenio::text, '') = '')) 
and 	((nr_seq_agrupamento	= obter_agrupamento_setor(cd_setor_prescr_p)) or (coalesce(nr_seq_agrupamento::text, '') = '')) 
order by coalesce(cd_especialidade,0), 
	coalesce(ie_tipo_atendimento,0), 
	coalesce(cd_setor_atendimento,0), 
	coalesce(cd_classif_setor_pac,0), 
	coalesce(cd_perfil,0), 
	coalesce(ie_clinica,0), 
	coalesce(cd_setor_prescr,0), 
	coalesce(cd_convenio,0), 
	coalesce(ie_tipo_convenio,0), 
	nr_sequencia;


BEGIN 
 
select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END  
into STRICT	ds_retorno_w 
from	procedimento_rotina_lib 
where 	nr_seq_proc_rotina = nr_sequencia_p;
 
if (coalesce(cd_perfil_p::text, '') = '') then 
	cd_perfil_w := obter_perfil_ativo;
else 
	cd_perfil_w := cd_perfil_p;
end if;
 
if (ds_retorno_w = 'N') then 
	open C01;
	loop 
	fetch C01 into	 
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ds_retorno_w := ds_retorno_w;	
		 
	end loop;
	close C01;
end if;	
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
