CREATE OR REPLACE FUNCTION obter_se_proc_terap_resp_pend (nr_atendimento_p text) RETURNS varchar AS $body$
DECLARE


	ie_proc_terap_resp_pend_w		varchar(1);


BEGIN

	select	coalesce(max('S'),'N')
	into STRICT	ie_proc_terap_resp_pend_w
	from	prescr_medica a,
			prescr_procedimento x,
			procedimento y,
			proc_interno c
	where	a.nr_prescricao = x.nr_prescricao
	and		x.cd_procedimento = y.cd_procedimento
	and		x.ie_origem_proced = y.ie_origem_proced
	and		x.cd_procedimento = c.cd_procedimento
	and		x.ie_origem_proced = c.ie_origem_proced
	and		a.nr_atendimento = nr_atendimento_p
	and		a.dt_inicio_prescr > clock_timestamp() - interval '7 days'
	and		c.ie_tipo in ('PTR')
	and		obter_se_prescr_vig_adep(a.dt_inicio_prescr,a.dt_validade_prescr,establishment_timezone_utils.startOfDay(clock_timestamp() - interval '5 days'),establishment_timezone_utils.startOfDay(clock_timestamp() + interval '1 days')) = 'S'
	and		coalesce(x.dt_suspensao::text, '') = ''
	and		coalesce(a.dt_suspensao::text, '') = ''
	and		coalesce(x.nr_seq_exame::text, '') = ''
	and		coalesce(x.nr_seq_solic_sangue::text, '') = ''
	and		coalesce(x.nr_seq_derivado::text, '') = ''
	and		coalesce(x.nr_Seq_origem::text, '') = ''
	and		coalesce(x.nr_seq_exame_sangue::text, '') = ''
	and		exists (	SELECT	1
				from	prescr_proc_hor	y
				where	y.nr_prescricao	= x.nr_prescricao
				and		x.nr_sequencia	= y.nr_seq_procedimento
				and		coalesce(y.ie_situacao,'A') = 'A'
				and		Obter_se_horario_liberado(y.dt_lib_horario, y.dt_horario) = 'S'
				and		coalesce(y.dt_suspensao::text, '') = ''
				and		coalesce(y.dt_fim_horario::text, '') = '')  LIMIT 1;

	return	ie_proc_terap_resp_pend_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
