CREATE OR REPLACE FUNCTION obter_se_produto_recebimento (nr_seq_caixa_rec_p bigint, nr_seq_produto_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w		varchar(10);
nr_titulo_w		bigint;
nr_adiantamento_w	bigint;	
count_w			bigint := 0;		
			 

BEGIN 
 
ds_retorno_w	:= 'N';
 
select	max(nr_titulo) 
into STRICT	nr_titulo_w 
from	titulo_receber_liq 
where	nr_seq_caixa_rec	= nr_seq_caixa_rec_p;
 
select	max(nr_adiantamento) 
into STRICT	nr_adiantamento_w 
from	adiantamento 
where	nr_seq_caixa_rec	= nr_seq_caixa_rec_p;
 
if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then	 
	select	count(*) 
	into STRICT	count_w 
	from	titulo_receber_classif 
	where	nr_titulo	= nr_titulo_w 
	and	nr_seq_produto	= nr_seq_produto_p;
	 
	if (count_w > 0) then 
		ds_retorno_w	:= 'S';
	end if;
elsif (nr_adiantamento_w IS NOT NULL AND nr_adiantamento_w::text <> '') then 
	select	count(*) 
	into STRICT	count_w 
	from	adiantamento_classif 
	where	nr_adiantamento	= nr_adiantamento_w 
	and	nr_seq_produto	= nr_seq_produto_p;
	 
	if (count_w > 0) then 
		ds_retorno_w	:= 'S';
	end if;
end if;
 
return 	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
