SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_prod_perfil (nr_seq_grupo_p bigint, cd_perfil_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);


BEGIN

ds_retorno_w	:= 'N';

if (coalesce(nr_seq_grupo_p,0) > 0) then

	select	coalesce(max('N'),'S')
	into STRICT	ds_retorno_w
	from	grupo_prod_financ_perfil
	where	nr_seq_grupo	= nr_seq_grupo_p;

	if (ds_retorno_w	= 'N') then

		select	coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	grupo_prod_financ_perfil
		where	nr_seq_grupo		= nr_seq_grupo_p
		and	cd_perfil		= cd_perfil_p
		and	coalesce(ie_acesso,'S')	= 'S';

	end if;
else

	/*Verifica as regras existentes para os perfis onde não existe grupo informado - para definir se o perfil permite consultar por exemplo títulos que não possuem grupo informado*/

	select	coalesce(max(ie_acesso),'S')
	into STRICT	ds_retorno_w
	from	grupo_prod_financ_perfil
	where	cd_perfil	= cd_perfil_p
	and	coalesce(nr_seq_grupo::text, '') = ''  LIMIT 1;

end if;

return 	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_prod_perfil (nr_seq_grupo_p bigint, cd_perfil_p bigint) FROM PUBLIC;

