CREATE OR REPLACE FUNCTION obter_se_prod_solub ( cd_material_p nutricao_leite_deriv.cd_material%type) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);

BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_retorno_w
from	nutricao_leite_deriv_solub a,
		nutricao_leite_deriv b
where	b.nr_sequencia = a.nr_seq_produto
and		coalesce(a.ie_situacao,'A') = 'A'
and 	coalesce(b.ie_situacao,'A') = 'A'
and		b.cd_material = cd_material_p;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
