SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_prof_escala_agenda (cd_agenda_p bigint, cd_medico_p text, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'S';
qt_registro_w	bigint;


BEGIN


select	count(*)
into STRICT	qt_registro_w
from	agenda_escala
where	cd_agenda = cd_agenda_p;

if (qt_registro_w > 0) then
	begin
	select	'S'
	into STRICT	ie_retorno_w
	from	agenda_escala a,
		escala_diaria b
	where	a.nr_seq_escala = b.nr_seq_escala
	and	a.cd_agenda = cd_agenda_p
	and	dt_agenda_p between dt_inicio and dt_fim
	and	b.cd_pessoa_fisica = cd_medico_p  LIMIT 1;
	exception
	when no_data_found then
		begin
		select	'S'
		into STRICT	ie_retorno_w
		from	agenda_escala a,
			escala_diaria b,
			escala_diaria_adic c
		where	a.nr_seq_escala = b.nr_seq_escala
		and	c.nr_seq_escala_diaria = b.nr_sequencia
		and	a.cd_agenda = cd_agenda_p
		and	dt_agenda_p between dt_inicio and dt_fim
		and	c.cd_pessoa_fisica = cd_medico_p  LIMIT 1;
		exception
		when no_data_found then
		ie_retorno_w	:= 'N';
		end;
	end;

end if;
return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
