CREATE OR REPLACE FUNCTION obter_se_prof_gestao ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_proc_interv_p bigint, nr_prescr_sae_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
nr_seq_equipe_w		bigint;

C01 CURSOR FOR
SELECT	nr_seq_equipe
from	sp_prescr_proc
where	nr_prescricao = nr_prescricao_p
and	nr_seq_prescr = nr_seq_prescr_p
order by nr_seq_equipe;

C02 CURSOR FOR
SELECT	nr_seq_equipe
from	sp_prescr_proc
where	nr_seq_pe_prescr = nr_prescr_sae_p
and	nr_seq_proc_interv = nr_seq_proc_interv_p
order by nr_seq_equipe;


BEGIN

if (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then

	open C01;
	loop
	fetch C01 into
		nr_seq_equipe_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		select coalesce(max('S'), 'N')
		into STRICT	ds_retorno_w
		from	sp_prof_equipe
		where	nr_seq_equipe = nr_seq_equipe_w
		and	obter_usuario_pf(cd_pessoa_fisica) = nm_usuario_p;

		if (ds_retorno_w = 'S') then
			exit;
		end if;

	end;
	end loop;
	close C01;

elsif (nr_seq_proc_interv_p IS NOT NULL AND nr_seq_proc_interv_p::text <> '') then

	open C02;
	loop
	fetch C02 into
		nr_seq_equipe_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
		select coalesce(max('S'), 'N')
		into STRICT	ds_retorno_w
		from	sp_prof_equipe
		where	nr_seq_equipe = nr_seq_equipe_w
		and	obter_usuario_pf(cd_pessoa_fisica) = nm_usuario_p;

		if (ds_retorno_w = 'S') then
			exit;
		end if;

	end;
	end loop;
	close C02;

end if;

return	ds_retorno_w;

end;
