CREATE OR REPLACE FUNCTION obter_se_proj_filtro_fluxo ( nr_seq_proj_rec_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


cont_w				bigint;
ds_retorno_w			varchar(10);


BEGIN

ds_retorno_w		:= 'S';

select	count(*)
into STRICT	cont_w
from	w_filtro_fluxo a
where	a.nm_usuario	= nm_usuario_p
and	(a.nr_seq_proj_rec IS NOT NULL AND a.nr_seq_proj_rec::text <> '')
and	coalesce(a.cd_banco::text, '') = '';

if (cont_w > 0) then

	ds_retorno_w		:= 'N';

	select	count(*)
	into STRICT	cont_w
	from	w_filtro_fluxo a
	where	a.nm_usuario	= nm_usuario_p
	and	a.nr_seq_proj_rec	= nr_seq_proj_rec_p
	and	coalesce(a.cd_banco::text, '') = '';

	if (cont_w > 0) then
		ds_retorno_w	:= 'S';
	end if;

end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
