CREATE OR REPLACE FUNCTION obter_se_proj_pendente ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(10)	:= 'N';
qt_consistencia_w	bigint;


BEGIN 
 
select 	coalesce(nr_pendencias,0)nr_pendencias 
into STRICT	qt_consistencia_w 
from (	SELECT	count(*)nr_pendencias 
	from	iniciacao_repasse_v 
	where	nr_sequencia = nr_sequencia_p 
	
union
 
	SELECT	count(*) 
	from	iniciacao_acordo_v 
	where	nr_sequencia = nr_sequencia_p 
	
union
 
	select	count(*) 
	from	planejamento_risco_v 
	where	nr_sequencia = nr_sequencia_p 
	
union
 
	select	count(*) 
	from	planejamento_cronograma_v 
	where	nr_sequencia = nr_sequencia_p 
	
union
 
	select	count(*) 
	from	planejamento_implement_v 
	where	nr_sequencia = nr_sequencia_p 
	
union
 
	select	count(*) 
	from	planejamento_consult_v 
	where	nr_sequencia = nr_sequencia_p 
	
union
 
	select	count(*) 
	from	execucao_mpo_v 
	where	nr_sequencia = nr_sequencia_p 
	
union
 
	select	count(*) 
	from	execucao_treinamento_v 
	where	nr_sequencia = nr_sequencia_p 
	
union
 
	select	count(*) 
	from	execucao_manual_v 
	where	nr_sequencia = nr_sequencia_p 
	
union
 
	select	count(*) 
	from	execucao_oficial_v 
	where	nr_sequencia = nr_sequencia_p 
	
union
 
	select	count(*) 
	from	execucao_final_v 
	where	nr_sequencia = nr_sequencia_p 
	
union
 
	select	count(*) 
	from	execucao_equipes_v 
	where	nr_sequencia = nr_sequencia_p 
	
union
 
	select	count(*) 
	from	controle_acomp_risco_v 
	where	nr_sequencia = nr_sequencia_p 
	
union
 
	select	count(*) 
	from	controle_status_report_v 
	where	nr_sequencia = nr_sequencia_p) x 
where	nr_pendencias > 0;
 
 
if (qt_consistencia_w > 0) then 
  ds_retorno_w	:=	'S';
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
