CREATE OR REPLACE FUNCTION obter_se_proj_recurso_lib ( nr_sequencia_p bigint, nm_usuario_p text default null, cd_estabelecimento_p bigint default null) RETURNS varchar AS $body$
DECLARE


nm_usuario_w		varchar(15)	:= coalesce(nm_usuario_p,wheb_usuario_pck.get_nm_usuario);
--cd_estabelecimento_w	number(4,0)	:= nvl(cd_estabelecimento_p,nvl(wheb_usuario_pck.get_cd_estabelecimento,0));
ie_retorno_w		varchar(1)	:= 'S';
qt_existe_w		integer;
nm_usuario_lib_w	projeto_recurso_lib_usu.nm_usuario_lib%type;

C01 CURSOR FOR
	SELECT	coalesce(ie_permite,'S'),nm_usuario_lib
	from	projeto_recurso_lib_usu
	where	coalesce(nm_usuario_lib,nm_usuario_w) = nm_usuario_w
	and	nr_seq_projeto = nr_sequencia_p
	order by coalesce(ie_permite,'S'),nm_usuario_lib;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	projeto_recurso_lib_usu
where	coalesce(nm_usuario_lib,nm_usuario_w) = nm_usuario_w
and	nr_seq_projeto = nr_sequencia_p;

if (qt_existe_w > 0) then
	open C01;
	loop
	fetch C01 into
		ie_retorno_w,
		nm_usuario_lib_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ie_retorno_w := ie_retorno_w;
		end;
	end loop;
	close C01;


/*elsif (qt_existe_w = 0) then
	begin
	select	'S'
	into	ie_retorno_w
	from	projeto_recurso
	where	(nvl(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w)
	and	nr_sequencia = nr_sequencia_p;
	exception
	when others then
		ie_retorno_w := 'N';
	end;*/
end if;

return	ie_retorno_w;

end;
$body$
