SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_proj_rec_lib_mod ( nr_seq_mod_compra_p bigint, nr_seq_proj_rec_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w			varchar(1) := 'X';
qt_registros_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	proj_rec_lib_mod_compra
where	nr_seq_mod_compra = nr_seq_mod_compra_p;

if (qt_registros_w > 0) then

	select	coalesce(max(ie_liberado),'X')
	into STRICT	ie_liberado_w
	from	proj_rec_lib_mod_compra
	where	nr_seq_mod_compra = nr_seq_mod_compra_p
	and	nr_seq_proj_rec = nr_seq_proj_rec_p;

	if (ie_liberado_w = 'X') then
		select	count(*)
		into STRICT	qt_registros_w
		from	proj_rec_lib_mod_compra
		where	nr_seq_mod_compra = nr_seq_mod_compra_p
		and	coalesce(nr_seq_proj_rec::text, '') = ''
		and	coalesce(ie_liberado,'S') = 'S';

		if (qt_registros_w = 0) then
			ie_liberado_w := 'N';
		else
			ie_liberado_w := 'S';
		end if;
	end if;
else
	ie_liberado_w := 'S';
end if;

return	ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
