CREATE OR REPLACE FUNCTION obter_se_prontuario_setor ( nr_prontuario_p bigint, cd_setor_atendimento_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(160) := 'N';
cd_setor_atendimento_w	bigint;
cd_setor_atual_w	bigint;

/*
S -  Esta no setor solicitante
N - Esta no SAME
E - Esta em outro setor */
C01 CURSOR FOR
SELECT	cd_setor_atendimento
FROM	same_cpi_emp
WHERE	nr_prontuario		= nr_prontuario_p
AND	cd_estabelecimento	= cd_estabelecimento_p
AND	coalesce(dt_devolucao::text, '') = ''
ORDER BY dt_emprestimo;


BEGIN

cd_setor_atual_w	:= null;
OPEN C01;
LOOP
FETCH C01 INTO
	cd_setor_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	cd_setor_atual_w	:= cd_setor_atendimento_w;
END LOOP;
CLOSE C01;

if (cd_setor_atual_w IS NOT NULL AND cd_setor_atual_w::text <> '') then
	ds_retorno_w	:= 'S';
	if (cd_setor_atual_w <> cd_setor_atendimento_p) then
		ds_retorno_w	:= 'E';
	end if;
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
