SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_pront_estab ( nr_documento_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter se o prontuário informado pertence ao estabelecimento informado
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_regra_pront_w		varchar(15);
ds_retorno_w			varchar(255);


BEGIN
select	coalesce(max(vl_parametro),'BASE')
into STRICT	ie_regra_pront_w
from	funcao_parametro
where	cd_funcao	= 0
and	nr_sequencia	= 120;

if (ie_regra_pront_w = 'BASE') then
	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pessoa_fisica x
	where	x.nr_prontuario		= nr_documento_p
	and	x.cd_estabelecimento	= cd_estabelecimento_p;
elsif (ie_regra_pront_w = 'ESTAB') and (coalesce(cd_estabelecimento_p,0) <> 0) then
	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pessoa_fisica_pront_estab x
	where	x.nr_prontuario		= nr_documento_p
	and	x.cd_estabelecimento	= cd_estabelecimento_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_pront_estab ( nr_documento_p bigint, cd_estabelecimento_p bigint) FROM PUBLIC;

