CREATE OR REPLACE FUNCTION obter_se_pront_oftalmo (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1) := 'N';
qt_consulta_w	bigint;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	select	1
	into STRICT	qt_consulta_w
	from 	oft_consulta_medica a,
		atendimento_paciente c
	where  	a.nr_atendimento        = c.nr_atendimento
	and     c.cd_pessoa_fisica      = cd_pessoa_fisica_p  LIMIT 1;
	exception
	when others then
		qt_consulta_w := 0;
	end;

	if (qt_consulta_w = 0) then
		begin
		select	1
		into STRICT	qt_consulta_w
		from	oft_consulta b
		where	b.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	coalesce(b.dt_cancelamento::text, '') = ''  LIMIT 1;
		exception
		when others then
			qt_consulta_w := 0;
		end;
	end if;

	if (qt_consulta_w = 0) then
		begin
		select	1
		into STRICT	qt_consulta_w
		from    oft_consulta b, atendimento_paciente c, pessoa_fisica d
		where   b.nr_atendimento   = c.nr_atendimento
		and     c.cd_pessoa_fisica = d.cd_pessoa_fisica
		and     c.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	  coalesce(b.dt_cancelamento::text, '') = ''  LIMIT 1;
		exception
		when others then
			qt_consulta_w := 0;
		end;
	end if;

	if (qt_consulta_w > 0) then
		ie_retorno_w := 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
