CREATE OR REPLACE FUNCTION obter_se_pront_solic_agenda (cd_pessoa_fisica_p text, cd_agenda_p bigint, nr_seq_agenda_p bigint, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_gerar_solic_w		varchar(1) := 'N';
cd_estabelecimento_w		smallint;
cd_setor_atendimento_w	integer;
nr_prontuario_w		bigint;
nr_seq_same_w			bigint;


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') then

	/* obter prontuário */

	select	coalesce(max(nr_prontuario),0)
	into STRICT	nr_prontuario_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (nr_prontuario_w	> 0) then
		/* obter dados agenda */

		select	max(cd_estabelecimento),
			max(cd_setor_exclusivo)
		into STRICT	cd_estabelecimento_w,
			cd_setor_atendimento_w
		from	agenda
		where	cd_agenda = cd_agenda_p;

		/* obter solicitações */

		select	CASE WHEN coalesce(count(*),0)=0 THEN  'S'  ELSE 'N' END
		into STRICT	ie_gerar_solic_w
		from	same_cpi_solic
		where	cd_estabelecimento		= cd_estabelecimento_w
		and	cd_setor_atendimento		= cd_setor_atendimento_w
		and	cd_agenda			= cd_agenda_p
		and	trunc(dt_desejada,'dd')	= trunc(dt_agenda_p,'dd')
		and	nr_prontuario			= nr_prontuario_w;

	end if;
end if;

return ie_gerar_solic_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
