DECLARE


ie_liberado_w		varchar(1) := 'S';


BEGIN

if (cd_protocolo_p IS NOT NULL AND cd_protocolo_p::text <> '') then

	select  coalesce(max('S'), 'N')
	into STRICT	ie_liberado_w
	from	protocolo where		cd_estabelecimento = cd_estabelecimento_p
	and		cd_protocolo = cd_protocolo_p LIMIT 1;

	if (ie_liberado_w = 'N') then
		select  coalesce(max('S'), 'N')
		into STRICT	ie_liberado_w
		from	protocolo where		coalesce(cd_estabelecimento::text, '') = ''
		and		cd_protocolo = cd_protocolo_p LIMIT 1;

		if (ie_liberado_w = 'S') then
			select  coalesce(max('N'), 'S')
			into STRICT	ie_liberado_w
			from	protocolo_lib_estab where		cd_protocolo = cd_protocolo_p LIMIT 1;
		end if;
	end if;

	if (ie_liberado_w = 'N') then
		select  coalesce(max('S'), 'N')
		into STRICT	ie_liberado_w
		from	protocolo_lib_estab where		cd_protocolo = cd_protocolo_p
		and		cd_estabelecimento = cd_estabelecimento_p LIMIT 1;
	end if;
end if;

return	ie_liberado_w;

end;
