CREATE OR REPLACE FUNCTION obter_se_protocolo_lib_proced (cd_protocolo_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w			varchar(01)	:= 'N';
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_seq_proc_interno_w		bigint;
nr_seq_exame_w			bigint;

c01 CURSOR FOR
	SELECT	cd_procedimento,
		ie_origem_proced,
		nr_seq_proc_interno,
		nr_seq_exame
	from	prescr_procedimento
	where	nr_prescricao	= nr_prescricao_p;


BEGIN

ie_liberado_w	:= 'N';

open	c01;
loop
fetch	c01 into
	cd_procedimento_w,
	ie_origem_proced_w,
	nr_seq_proc_interno_w,
	nr_seq_exame_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (ie_liberado_w = 'N') then
		begin

		if (nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '') then
			select	coalesce(max('S'),'N')
			into STRICT	ie_liberado_w
			from	protocolo_proc_interno
			where	cd_protocolo		= cd_protocolo_p
			and	nr_seq_proc_interno	= coalesce(nr_seq_proc_interno_w,0);
		end if;

		if (ie_liberado_w = 'N') then
			select	coalesce(max('S'),'N')
			into STRICT	ie_liberado_w
			from	protocolo_procedimento
			where	cd_protocolo		= cd_protocolo_p
			and	cd_procedimento	= cd_procedimento_w
			and	ie_origem_proced	= ie_origem_proced_w;
		end if;

		if (ie_liberado_w = 'N') then
			select	coalesce(max('S'),'N')
			into STRICT	ie_liberado_w
			from	protocolo_exame
			where	cd_protocolo		= cd_protocolo_p
			and	nr_seq_exame		= nr_seq_exame_w;
		end if;

		end;
	end if;
end loop;
close c01;

return ie_liberado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
