CREATE OR REPLACE FUNCTION obter_se_protocolo_lib_setor ( cd_protocolo_p bigint, cd_setor_atendimento_p bigint, cd_medico_p text, ie_tipo_convenio_p bigint default null, nm_usuario_p text default null, cd_convenio_p bigint default null) RETURNS varchar AS $body$
DECLARE


ie_regra_lib_w	varchar(1);
cd_medico_w		varchar(10);
cd_perfil_w		perfil.cd_perfil%type;
ds_sql_w		varchar(4000);


BEGIN

if (cd_protocolo_p IS NOT NULL AND cd_protocolo_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_regra_lib_w
	from	protocolo_setor where		cd_protocolo	= cd_protocolo_p LIMIT 1;

	if (ie_regra_lib_w = 'S') then
		cd_medico_w	:= cd_medico_p;

		if (coalesce(cd_medico_p::text, '') = '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
			select	max(cd_pessoa_fisica)
			into STRICT	cd_medico_w
			from	usuario
			where	nm_usuario	= nm_usuario_p;
		end if;

		ds_sql_w	:=
			' select	nvl(max(''S''),''N'') ' ||
			' from		protocolo_setor ' ||
			' where		rownum = 1 ' ||
			' and		cd_protocolo = ' || cd_protocolo_p || ' ';

		if (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') then
			ds_sql_w	:= ds_sql_w || ' and ((cd_especialidade is null) or (obter_se_especialidade_medico( '||''''||cd_medico_w||''''||', cd_especialidade) = ''S'')) ';
		end if;

		if (ie_tipo_convenio_p IS NOT NULL AND ie_tipo_convenio_p::text <> '') then
			ds_sql_w	:= ds_sql_w || ' and ((ie_tipo_convenio = '||''''||ie_tipo_convenio_p||''''||') or (ie_tipo_convenio is null)) ';
		end if;

		ds_sql_w	:= ds_sql_w || ' and nvl(cd_setor_atendimento,' || coalesce(cd_setor_atendimento_p,0) || ') = ' || coalesce(cd_setor_atendimento_p,0) || ' ';
		ds_sql_w	:= ds_sql_w || ' and nvl(cd_perfil,' || coalesce(wheb_usuario_pck.get_cd_perfil,0) || ') = ' || coalesce(wheb_usuario_pck.get_cd_perfil,0) || ' ';
		ds_sql_w	:= ds_sql_w || ' and nvl(cd_perfil,' || coalesce(wheb_usuario_pck.get_cd_perfil,0) || ') = ' || coalesce(wheb_usuario_pck.get_cd_perfil,0) || ' ';
		ds_sql_w	:= ds_sql_w || ' and nvl(cd_convenio, ' || coalesce(cd_convenio_p,0) || ') = ' || coalesce(cd_convenio_p,0) || ' ';

		EXECUTE ds_sql_w into STRICT ie_regra_lib_w;

		return	ie_regra_lib_w;
	end if;
end if;

return	'S';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
