CREATE OR REPLACE FUNCTION obter_se_protocolo_perda ( Nr_Seq_Protocolo_p Protocolo_Convenio.Nr_Seq_protocolo%Type) RETURNS varchar AS $body$
DECLARE

 
	Ds_Retorno_w	varchar(1) := 'N';
				

BEGIN 
 
If (coalesce(Nr_Seq_Protocolo_p,0) > 0) Then 
 
	Select 	coalesce(Max('S'),'N') 
	Into STRICT	Ds_Retorno_w 
	From	Protocolo_Convenio b 
	Where 	b.Nr_Seq_Protocolo = Nr_Seq_Protocolo_p 
	And	b.Ie_Status_Protocolo = 4; --Identifica se Protocolo de Perda 
End If;
 
Return	Ds_Retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
