qt_registros_w		bigint	:= 0;



BEGIN
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then

	select	count(*)
	into STRICT	qt_registros_w
	from	titulo_receber
