SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_protoc_onc_lib ( cd_protocolo_p text, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_Retorno_w varchar(1)	:='S';
qt_reg_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	PROTOCOLO_REGRA_ONC
where	cd_protocolo		= cd_protocolo_p
and	coalesce(ie_situacao,'A')	= 'A';

if (qt_reg_w > 0) then
	select	count(*)
	into STRICT	qt_reg_w
	from	protocolo_regra_onc a
	where	a.cd_protocolo		= cd_protocolo_p
	and	coalesce(ie_situacao,'A')	= 'A'
	and	exists (SELECT	1
			 from	can_loco_regional b,
				cid_doenca c
			 where	b.cd_doenca_cid	   = c.cd_doenca_cid
			 and	b.cd_pessoa_fisica = cd_pessoa_fisica_p
			 and	a.cd_categoria_cid = c.cd_categoria_cid
			 and	coalesce(b.dt_inativacao::text, '') = '');

	if (qt_reg_w = 0) then
		ie_Retorno_w	:= 'N';
	end if;
end if;

return	ie_Retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
