CREATE OR REPLACE FUNCTION obter_se_prot_lib_estab_conv ( cd_protocolo_p regra_prot_medic_conv_onc.cd_protocolo%type, cd_estabelecimento_p regra_prot_medic_conv_onc.cd_estabelecimento%type, cd_convenio_p regra_prot_medic_conv_onc.cd_convenio%type) RETURNS varchar AS $body$
DECLARE

			
ie_liberado_w varchar(1)  := 'S';
qtd_registros_w integer  := 0;


BEGIN
if (cd_protocolo_p IS NOT NULL AND cd_protocolo_p::text <> '') then

	select  count(*)
	into STRICT 	qtd_registros_w
	from	regra_prot_medic_conv_onc where   cd_protocolo = cd_protocolo_p
	and 	cd_estabelecimento = cd_estabelecimento_p
  and   cd_convenio = cd_convenio_p
	and 	ie_restringe_exibicao = 'S' LIMIT 1;

	if (qtd_registros_w > 0) then
		ie_liberado_w := 'N';
	else
		ie_liberado_w := 'S';
	end if;

end if;

return	ie_liberado_w;

end;
$body$
