CREATE OR REPLACE FUNCTION obter_se_prot_onc_convenio ( cd_protocolo_p bigint, nr_seq_medicacao_p bigint, cd_convenio_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberado_w		regra_prot_medic_conv_onc.ie_restringe_exibicao%type := 'S';
cont_w				bigint;
cd_medico_w			varchar(10);

c01 CURSOR FOR
SELECT	CASE WHEN ie_restringe_exibicao='S' THEN  'N'  ELSE 'S' END  ie_restringe
from	regra_prot_medic_conv_onc
where	cd_protocolo	 = cd_protocolo_p
and		nr_seq_medicacao = nr_seq_medicacao_p
and (coalesce(cd_convenio::text, '') = '' or cd_convenio = cd_convenio_p)
and (coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0))
and (coalesce(cd_perfil,coalesce(wheb_usuario_pck.get_cd_perfil,0)) = coalesce(wheb_usuario_pck.get_cd_perfil,0))
and 	((wheb_usuario_pck.get_cd_estabelecimento = cd_estabelecimento) or (coalesce(cd_estabelecimento::text, '') = '' and ie_somente_estabelecimento = 'N'))
order by coalesce(cd_protocolo, 0),
    coalesce(nr_seq_medicacao, 0),
    coalesce(cd_convenio, 0);

BEGIN

select	count(*)
into STRICT	cont_w
from	regra_prot_medic_conv_onc
where	cd_protocolo	= cd_protocolo_p
and	    nr_seq_medicacao = nr_seq_medicacao_p
and (coalesce(cd_convenio::text, '') = '' or cd_convenio = cd_convenio_p)
and 	((wheb_usuario_pck.get_cd_estabelecimento = cd_estabelecimento) or (ie_somente_estabelecimento = 'N'));

if (cont_w > 0) then

    for registro in c01 loop
        ie_liberado_w := registro.ie_restringe;
    end loop;
	
end if;

return	ie_liberado_w;

end;
$body$
