CREATE OR REPLACE FUNCTION obter_se_prot_sae_saps_cp_lib ( nr_seq_protocolo_p bigint default null, nr_seq_modelo_p bigint default null, nr_atendimento_p bigint default null, cd_perfil_p bigint default null, nm_usuario_p text default null, cd_setor_atendimento_p bigint default null, cd_estabelecimento_p bigint default null) RETURNS varchar AS $body$
DECLARE


ie_retorno_w   varchar(10):= 'N';
qt_retorno_w   bigint;

cd_estabelecimento_w      pe_protocolo_regra.cd_estabelecimento%type;
cd_setor_atendimento_w	  pe_protocolo_regra.cd_setor_atendimento%type;
cd_perfil_w	              pe_protocolo_regra.cd_perfil%type;
			

BEGIN

  cd_perfil_w := coalesce(cd_perfil_p, -1);
  cd_setor_atendimento_w := coalesce(cd_setor_atendimento_p, -1);
  cd_estabelecimento_w := coalesce(cd_estabelecimento_p, -1);

  if (coalesce(nr_seq_modelo_p::text, '') = '' or length(nr_seq_modelo_p) = 0) then

      select count(1)
      into STRICT qt_retorno_w
      from pe_protocolo_regra 
      where nr_seq_protocolo = nr_seq_protocolo_p
      and (nr_seq_modelo IS NOT NULL AND nr_seq_modelo::text <> '');

      if (qt_retorno_w = 0) then
      -- protocolo nao possui nenhum modelo vinculado, entao mostrar
        ie_retorno_w := 'S';
      else
      -- protocolo possui modelos vinculados, entao nao mostrar
        ie_retorno_w := 'N';
      end if;
  else

    select count(1)
    into STRICT qt_retorno_w
    from pe_protocolo_regra
    where nr_seq_protocolo = nr_seq_protocolo_p
    and   nr_seq_modelo = nr_seq_modelo_p
    and   coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w
    and   coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w
    and   coalesce(cd_setor_atendimento,cd_setor_atendimento_w) = cd_setor_atendimento_w;

    if (qt_retorno_w > 0) then
    -- esse protocolo tem o modelo selecionado cadastrado como liberado
      ie_retorno_w := 'S';
    else

      select count(1)
      into STRICT qt_retorno_w
      from pe_protocolo_regra 
      where nr_seq_protocolo = nr_seq_protocolo_p
      and (nr_seq_modelo IS NOT NULL AND nr_seq_modelo::text <> '');

      if (qt_retorno_w = 0) then
      -- protocolo nao possui nenhum modelo vinculado, entao mostrar
        ie_retorno_w := 'S';
      else
      -- protocolo possui modelos vinculados, entao nao mostrar
        ie_retorno_w := 'N';
      end if;

    end if;
  end if;

  return	ie_retorno_w;

end;
$body$
