SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_pr_fleury_diag_seq ( nr_prescricao_p bigint, nr_seq_prescr_p bigint ) RETURNS varchar AS $body$
DECLARE


cd_sigla_w		varchar(20) := null;
nr_seq_proc_interno_w	bigint;
nr_seq_prescr_w		bigint;
cd_estabelecimento_w	prescr_medica.cd_estabelecimento%type;
ie_tipo_atend_w		smallint;

C01 CURSOR FOR
	SELECT	p.nr_sequencia	
	from	prescr_procedimento p,
			proc_interno i,
			prescr_medica a
	where	a.nr_prescricao = p.nr_prescricao
	and p.nr_seq_proc_interno = i.nr_sequencia
	and	p.nr_prescricao	= nr_prescricao_p
	and	p.nr_sequencia = nr_seq_prescr_p
	and	coalesce(a.dt_liberacao_medico::text, '') = ''
	and	coalesce(p.nr_seq_exame::text, '') = ''
	and	i.ie_tipo not in ('AP','APH','APC');
	

BEGIN

open C01;
loop
fetch C01 into	
	nr_seq_prescr_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	select 	max(nr_seq_proc_interno)
	into STRICT	nr_seq_proc_interno_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_seq_prescr_w;
	
	if (nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '') then
		
		select	max(coalesce(b.cd_estabelecimento,0)),
			max(coalesce(a.ie_tipo_atendimento,0))
		into STRICT	cd_estabelecimento_w,
			ie_tipo_atend_w	
		from	atendimento_paciente a,
			prescr_medica b
		where	a.nr_atendimento = b.nr_atendimento
		and	b.nr_prescricao = nr_prescricao_p;
		
		select	max(cd_integracao)
		into STRICT	cd_sigla_w
		from	regra_proc_interno_integra
		where	nr_seq_proc_interno 	= nr_seq_proc_interno_w
		and	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
		and	coalesce(ie_tipo_atendimento, ie_tipo_atend_w) = ie_tipo_atend_w
		and	ie_tipo_integracao 	= 6;
			
	end if;

	if (cd_sigla_w IS NOT NULL AND cd_sigla_w::text <> '') then
		exit;
	end if;
	
	end;
end loop;
close C01;

if (coalesce(cd_sigla_w::text, '') = '') then
	cd_sigla_w := '0';
end if;

return	cd_sigla_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
