CREATE OR REPLACE FUNCTION obter_se_qtde_req_fracionada (cd_material_p bigint, qt_requisitada_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1);
qt_conv_estoque_consumo_w	double precision;
qt_fracao_w			double precision;
qt_multiplo_w			double precision;


BEGIN

ie_retorno_w	:= 'N';

select	qt_conv_estoque_consumo
into STRICT	qt_conv_estoque_consumo_w
from	material
where	cd_material = cd_material_p;

if (qt_requisitada_p < qt_conv_estoque_consumo_w) then
	ie_retorno_w	:= 'S';
else
	begin

	qt_multiplo_w	:= trunc(dividir(qt_requisitada_p, qt_conv_estoque_consumo_w),0);
	qt_fracao_w	:= qt_requisitada_p - (qt_multiplo_w * qt_conv_estoque_consumo_w);

	if (qt_fracao_w <> 0) then
		ie_retorno_w	:= 'S';
	end if;

	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
