SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_queixa_atend_lib (nr_seq_queixa_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(10) := 'S';
qt_regra_queixa_w	bigint;			
		

BEGIN

select	count(*)
into STRICT	qt_regra_queixa_w
from	QUEIXA_PACIENTE_LIB_ATEND
where nr_seq_queixa = nr_seq_queixa_p;

if (qt_regra_queixa_w > 0) then

	select	coalesce(max(ie_visivel),'SEMPERFIL')
	into STRICT	ie_retorno_w
	from	QUEIXA_PACIENTE_LIB_ATEND
	where nr_seq_queixa = nr_seq_queixa_p
	and cd_perfil = OBTER_PERFIL_ATIVO;
	
	if (ie_retorno_w = 'SEMPERFIL') then
		
		select	coalesce(max(ie_visivel),'N')
		into STRICT	ie_retorno_w
		from	QUEIXA_PACIENTE_LIB_ATEND
		where nr_seq_queixa = nr_seq_queixa_p
		and coalesce(cd_perfil::text, '') = '';
		
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
