CREATE OR REPLACE FUNCTION obter_se_reacao_alergenos ( nr_seq_alergenos_p bigint, nr_seq_reacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w         varchar(255) := 'S';
qt_reg_w			bigint;
cd_perfil_w			bigint := obter_perfil_ativo;

BEGIN

if (coalesce(nr_seq_reacao_p,0)  > 0) and (coalesce(nr_seq_alergenos_p,0) > 0) then

	select 	count(*)
	into STRICT 	qt_reg_w
	from 	tipo_alergia_reacao
	where	nr_seq_tipo = nr_seq_alergenos_p;


	if (qt_reg_w > 0) then

	   select	count(*)
	   into STRICT     qt_reg_w
	   from  	tipo_alergia_reacao
	   where	nr_seq_tipo = nr_seq_alergenos_p
	   and		nr_seq_reacao = nr_seq_reacao_p;

	  if (qt_reg_w = 0) then

			ds_retorno_w := 'N';

	  end if;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
