CREATE OR REPLACE FUNCTION obter_se_realiza_procprevisto ( nr_seq_evento_p bigint, nr_cirurgia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_executa_procedimentos_w 	varchar(1);
qt_cirurgia_w			bigint;
ie_realiza_proc_previsto_w	varchar(1) := 'N';


BEGIN
if (nr_seq_evento_p IS NOT NULL AND nr_seq_evento_p::text <> '') and (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	begin
	select	coalesce(max(ie_executa_procedimentos),'N')
	into STRICT	ie_executa_procedimentos_w
	from	evento_cirurgia
	where	nr_sequencia = nr_seq_evento_p;

	select	count(*)
	into STRICT	qt_cirurgia_w
	from	cirurgia
	where	nr_cirurgia =  nr_cirurgia_p
	and	(dt_inicio_real IS NOT NULL AND dt_inicio_real::text <> '');

	if (ie_executa_procedimentos_w = 'S') and (qt_cirurgia_w > 0) then
		begin
		ie_realiza_proc_previsto_w := 'S';
		end;
	end if;
	end;
end if;

return ie_realiza_proc_previsto_w;

end;
$body$
LANGUAGE PLPGSQL
