CREATE OR REPLACE FUNCTION obter_se_reavaliacao_medic ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_reavaliacao_w ATENDIMENTO_PACIENTE_AUX.IE_NECESSIDADE_RECONSULTA%type := 'N';



BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then
	
	select coalesce(max(IE_NECESSIDADE_RECONSULTA),'N')
	into STRICT	ie_reavaliacao_w
	from	ATENDIMENTO_PACIENTE_AUX
	where   nr_atendimento = nr_atendimento_p
	and  	IE_NECESSIDADE_RECONSULTA = 'S';
	
end if;

return ie_reavaliacao_w;

end 	;	
