CREATE OR REPLACE FUNCTION obter_se_receb_etapa (nm_usuario_p text, cd_perfil_p bigint, nr_seq_etapa_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  FATUR_ETAPA_RECEB.IE_LIBERADO%TYPE := 'N';
qt_regras_w	  bigint;

C01 CURSOR FOR
	  SELECT  coalesce(ie_liberado,'S')
	  FROM    FATUR_ETAPA_RECEB
      WHERE   coalesce(nm_usuario_receb,nm_usuario_p) = nm_usuario_p
	  AND     coalesce(cd_perfil,cd_perfil_p) = cd_perfil_p
	  AND  	  nr_seq_etapa = nr_seq_etapa_p
    ORDER BY  coalesce(nm_usuario_receb,' '), coalesce(cd_perfil,0);

BEGIN

SELECT COUNT(*)
INTO STRICT   qt_regras_w
FROM   FATUR_ETAPA_RECEB
WHERE  nr_seq_etapa = nr_seq_etapa_p;

IF (qt_regras_w = 0) THEN
   		ds_retorno_w := 'S';
ELSE
		OPEN C01;
		LOOP
		FETCH C01 INTO
		  ds_retorno_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */

			 ds_retorno_w := ds_retorno_w;

		END LOOP;
		CLOSE C01;
END IF;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
