CREATE OR REPLACE FUNCTION obter_se_receita_lib_pep (ie_tipo_receita_p text) RETURNS varchar AS $body$
DECLARE


ie_liberado_w	varchar(1) := 'S';
qt_reg_w	bigint;
cd_perfil_w	bigint;
cd_estabelecimento_w	bigint;

C01 CURSOR FOR
	SELECT	ie_permite
	from	tipo_receita_lib
	where	ie_tipo_receita = ie_tipo_receita_p
	and	coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w
	and	coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w
	order by coalesce(cd_estabelecimento,0),coalesce(cd_perfil,0);


BEGIN

cd_perfil_w := obter_perfil_Ativo;
cd_estabelecimento_w	:= obter_estabelecimento_ativo;

select	count(*)
into STRICT	qt_reg_w
from	tipo_receita_lib
where	ie_tipo_receita = ie_tipo_receita_p;

if (qt_reg_w > 0) then
/*
	select	count(*)
	into	qt_reg_w
	from	tipo_receita_lib
	where	ie_tipo_receita = ie_tipo_receita_p
	and	nvl(cd_perfil,cd_perfil_w) = cd_perfil_w;
	*/
	open C01;
	loop
	fetch C01 into
		ie_liberado_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;

end if;

return	ie_liberado_w;

end;
$body$
