CREATE OR REPLACE FUNCTION obter_se_recem_nasc ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

			 
ie_retorno_w		varchar(2);
			
 

BEGIN 
 
if (nr_atendimento_p <> 0) then 
	begin 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_retorno_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p 
	and	obter_idade_pf(cd_pessoa_fisica,clock_timestamp(),'DIA') <= 28;
	end;
end if;
 
return	ie_retorno_w;
 
end;
