CREATE OR REPLACE FUNCTION obter_se_recoleta_novo (nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_amostra_p text, ie_status_p text, ie_cosidera_nova_recoleta_p text, ie_status_recoleta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_result_w	varchar(1) := 'N';
qt_etapa_w	double precision;



BEGIN
if (ie_cosidera_nova_recoleta_p = 'N') then

	if (ie_amostra_p = 'N') and (ie_status_p >= 20) then
		ie_result_w := 'S';
	end if;

else
	if (ie_status_p = ie_status_recoleta_p) then

		if (ie_status_recoleta_p = 20) then

			if (ie_amostra_p = 'N') and (ie_status_p >= 20) then
				ie_result_w := 'S';
			end if;

		else
			select 	count(*)
			into STRICT	qt_etapa_w
			from 	prescr_proc_etapa
			where 	nr_prescricao  = nr_prescricao_p
			and 	nr_seq_prescricao  = nr_seq_prescr_p
			and	ie_etapa >= 20;

			if (qt_etapa_w > 0) then

				ie_result_w := 'S';

			end if;
		end if;
	end if;
end if;


return ie_result_w;

end;
$body$
